// Get the rule from the form submission
const rule = $('On form submission').first().json.rule;

// Collect all Excel data rows from current items
const excelData = $input.all();

// Format the Excel data as a table
let dataTable = '';
if (excelData.length > 0) {
  // Get column headers from the first item
  const headers = Object.keys(excelData[0].json);

  // Create header row
  dataTable += headers.join(' | ') + '\n';
  dataTable += headers.map(() => '---').join(' | ') + '\n';

  // Add data rows
  excelData.forEach(item => {
    const row = headers.map(header => item.json[header] || '').join(' | ');
    dataTable += row + '\n';
  });
}

// Build the comprehensive prompt
const prompt = `Please analyze the following data according to this rule: "${rule}"

Data to analyze:
${dataTable}

Based on the rule provided, please:
1. Classify the data into appropriate categories
2. Determine the optimal number of categories
3. Explain your classification logic
4. Provide a summary of what each category represents
5. Include any insights or patterns you observe in the data`;

// Return a single item with the complete prompt
return [{ json: { prompt: prompt } }];
